'use strict';

// Listings controller
angular.module('accounts').controller('AccountContactsController', [
    '$scope',
    '$stateParams',
    'dataFallback',
    'Contact',
    'CommonService',
    'gNotify',
	function($scope, $stateParams, dataFallback, Contact, CommonService, gNotify) {

		$scope.init = function(){
			$scope.contacts = Contact.query({accountId: $stateParams.accountId});
		};
	}
]);
