'use strict';

// accounts preferences controller
angular.module('accounts').controller('CustomerReceptionistsItttController', [
	'$scope',
    '$uibModal',
    'notify',
	function($scope, $uibModal, notify) {

        var self = this;

        self.openItem = function($index, item, frm){
            $uibModal.open({
                size: 'lg',
                templateUrl: 'app/accounts/views/account/receptionists/ittt.modal.client.view.html',
                controller: 'CustomerReceptionistsItttModalController as itttCtrl',
                resolve: {
                    ittt: function() {
                        return item;
                    }
                }
            }).result.then(function(result){
                if (!result.ifThis && !result.thenThat){
                    result.action = 'delete';
                }

                if (result.action === 'delete'){
                    $scope.account.receptionistsService.receptionDesk.ittt.splice($index, 1);
                    frm.$setSubmitted();
                    $scope.saveForm(frm, ['receptionistsService.receptionDesk.ittt']);
                } else if (result.action === 'update'){
                    $scope.account.receptionistsService.receptionDesk.ittt[$index].ifThis = result.ifThis;
                    $scope.account.receptionistsService.receptionDesk.ittt[$index].thenThat = result.thenThat;
                    frm.$setSubmitted();
                    $scope.saveForm(frm, ['receptionistsService.receptionDesk.ittt']);
                } else {
                    notify({message:'Error', classes: 'alert-danger'});
                }
            });
        };

        self.addNewItem = function(frm){
            if (!_.get($scope.account, 'receptionistsService.receptionDesk.ittt')){
                _.set($scope.account, 'receptionistsService.receptionDesk.ittt', []);
            }
            var item = {
                item: 'New item'
            };
            $scope.account.receptionistsService.receptionDesk.ittt.push(item);
            self.openItem($scope.account.receptionistsService.receptionDesk.ittt.length-1, item, frm);
        };
	}
]);
