'use strict';

// accounts preferences controller
angular.module('accounts').controller('CustomerReceptionistsScreeningController', [
	'$scope',
	function($scope) {

        $scope.addedPreBookingItem = function($event, frm){
            var newItem = $event.currentTarget.innerText;

            //is there anything to add?
            if (!newItem) {
                return;
            }
            //first init?
            if (!_.get($scope.account, 'receptionistsService.screening.checklist')){
                _.set($scope.account, 'receptionistsService.screening.checklist', []);
            }
            //unique validation
            if (_.find($scope.account.receptionistsService.screening.checklist, {'item': newItem})){
                $event.currentTarget.innerText = '';
                return;
            }
            //add new item
            $scope.account.receptionistsService.screening.checklist.push({
                item: newItem
            });

            //clean
            $event.currentTarget.innerText = '';

            if (frm){
                frm.$setDirty();
            }
        };

        $scope.editedPreBookingItem = function($index, $event, frm){
            $scope.account.receptionistsService.screening.checklist[$index].item = $event.currentTarget.innerText;
            if ($event.currentTarget.innerText === ''){
                $scope.account.receptionistsService.screening.checklist.splice($index, 1);
            }

            if (frm){
                frm.$setDirty();
            }
        };

	}
]);
