/* global moment, _, CKEDITOR, $ */
'use strict';

angular.module('accounts')
    .directive('fishIcon', function() {
        var template = '<img src="/app/accounts/img/fishicon-gray.png" data-ng-if="ngModel===0">' +
            '<img src="/app/accounts/img/fishicon.png" data-ng-repeat="i in getValue() track by $index">';
        return {
            restrict: 'E',
            scope: {
                ngModel: '='
            },
            template: template,
            link: function(scope, el, attr) {
                scope.getValue = function(){
                    if (!scope.ngModel) return false;
                    return new Array(scope.ngModel);
                };
            }
        };
    })
;
