'use strict';

//Accounts service used to communicate Accounts REST endpoints
angular.module('accounts').factory('Billing', [
    '$resource',
    'AppConfig',
    function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'accounts/:accountId/invoices/:action', {
            accountId: '@accountId',
            invoiceId: '@invoiceId'
        }, {
            invoiceItems: {
                url: baseApiOldUrl + 'accounts/:accountId/invoices/:invoiceId/items',
                isArray: true
            },
            list: {
                isArray: true
            },
            balance: {
                method: 'PUT',
                params: {
                    action: 'balance'
                }
            }
        });
    }
]);
