'use strict';

//Accounts service used to communicate Accounts REST endpoints
angular.module('accounts').factory('Contact', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;

        return $resource(baseApi2Url + 'contacts/:contactId', {
            contactId: '@_id'
        }, {
            query: {
                isArray: false
            },
            save: {
                method: 'PUT'
            }
        });
	}
]);
