'use strict';

//accounts invoice items service used to communicate customer's invoice items REST endpoints
angular.module('accounts').factory('AccountsInvoiceItems', [
    '$resource',
    'AppConfig',
    function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'accounts/:accountId/invoice-items/:invoiceItemId/:action', {
            invoiceItemId: '@_id',
            accountId: '@accountId'
        }, {
            update: {
                method: 'PUT'
            },
            editBalance: {
                method: 'POST',
                params: {
                    action: 'edit-balance'
                }
            }
        });
    }
]);
