'use strict';

//Accounts service used to communicate Accounts REST endpoints
angular.module('accounts').factory('Guest', ['' +
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {
        var baseApi2Url = AppConfig.API_URL;
        return $resource(baseApi2Url + 'guests/:guestId', { guestId: '@_id'
        }, {
            query: {
                isArray: false
            },
            update: {
                method: 'PUT'
            }
        });
	}
]);
