'use strict';

angular.module('accounts').factory('OneTimePayments', [
	'$resource',
	'AppConfig',
	function($resource, AppConfig){
		const baseApiUrl = AppConfig.API_URL;
		return $resource(baseApiUrl + 'one-time-payments/:otpId', { otpId: '@_id' }, {
			update: {
				method: 'PUT'
			}
		});
	}
]);
