'use strict';

//OwnersPortal service used to communicate OwnersPortal REST endpoints
angular.module('accounts').factory('OwnersPortal', [
	'$resource',
	'AppConfig',
	function($resource, AppConfig){
		const baseApiUrl = AppConfig.API_URL;
		return $resource(baseApiUrl + '/owners-portal/:ownersPortalId', { ownersPortalId: '@_id' }, {
			update: {
				method: 'PUT'
			},
			checkName: {
				url: baseApiUrl + '/check-subdomain-availability',
				method: 'POST'
			}
		});
	}
]);
