'use strict';

angular.module('accounts').factory('User', [
    '$resource',
    'AppConfig',
    function($resource, AppConfig) {
        var baseApi2Url = AppConfig.API_URL;
        return $resource(baseApi2Url + 'users/:userId', { userId: '@_id'
        }, {
            query: {
                isArray: false
            },
            save: {
                method: 'POST'
            },
            update: {
                method: 'PUT'
            },
            generateToken: {
                url: baseApi2Url + 'create-token/user',
                method: 'POST'
            }
        });
    }
]);
