'use strict';

// Configuring the Admin module
angular.module('admin').run(['Menus',
	function(Menus) {
		//Add admin menu item
		Menus.addMenuItem('topbar', {
			title: 'Admin',
			state: 'admin',
			roles: ['admin', 'shift-manager', 'receptionists-ticket-assigner', 'shifts-viewer'],
			position: 6,
			type: 'dropdown'
		});
		Menus.addSubMenuItem('topbar', 'admin', {
			title: 'Shifts Calendar',
			state: 'admin.shifts.calendar',
			roles: ['admin', 'shifts-viewer']
		});
		Menus.addSubMenuItem('topbar', 'admin', {
			title: 'Shifts List',
			state: 'admin.shifts.list',
            roles: ['admin', 'shifts-viewer']
		});
        Menus.addSubMenuItem('topbar', 'admin', {
            title: 'Dashboard tickets',
            state: 'admin.dashboard-tickets',
            roles: ['admin', 'shift-manager', 'receptionists-ticket-assigner']
        });
        Menus.addSubMenuItem('topbar', 'admin', {
            title: 'Static Metrics',
            state: 'admin.static-metrics',
            roles: ['admin']
        });
        Menus.addSubMenuItem('topbar', 'admin', {
            title: 'Employees',
            state: 'admin.employees',
            roles: ['admin']
        });

		//Create admin menu
		Menus.addMenu('admin', {
			items: [
                {
                    title: 'Shifts Calendar',
                    state: 'admin.shifts.calendar',
                    icon: 'calendar',
                    roles: ['admin', 'shifts-viewer']
                },
                {
                    title: 'Shifts List',
                    state: 'admin.shifts.list',
                    icon: 'th-list',
                    roles: ['admin', 'shifts-viewer']
                },
                {
                    title: 'Dashboard Tickets',
                    state: 'admin.dashboard-tickets',
                    roles: ['admin', 'shift-manager', 'receptionists-ticket-assigner']
                },
                {
                    title: 'Static Metrics',
                    state: 'admin.static-metrics',
                    roles: ['admin']
                },
                {
                    title: 'Employees',
                    state: 'admin.employees',
                    roles: ['admin']
                }
            ]
		});
	}
]);
