'use strict';

//Setting up route
angular.module('admin').config(['$stateProvider',
	function($stateProvider) {

		$stateProvider
            .state('admin', {
				abstract: true,
				url: '/admin',
				controller: 'AdminController',
				templateUrl: 'app/core/views/sidebar.client.view.html'
			})
            .state('admin.shifts', {
				abstract: true,
				url: '/shifts',
				template: '<ui-view/>'
			})
            .state('admin.shifts.calendar', {
				url: '/calendar',
				title: 'Calendar',
				templateUrl: 'app/admin/views/shifts-calendar.client.view.html'
			})
            .state('admin.shifts.list', {
				url: '/list',
				title: 'Shifts List',
				templateUrl: 'app/admin/views/shifts-list.client.view.html'
			})
            .state('admin.dashboard-tickets', {
                url: '/dashboard-tickets',
                title: 'Inbox Tickets',
                templateUrl: 'app/admin/views/dashboard-tickets.client.view.html'
            })
            .state('admin.static-metrics', {
                url: '/static-metrics',
                title: 'Static Metrics',
                templateUrl: 'app/admin/views/static-metrics.client.view.html'
            })
            .state('admin.employees', {
                url: '/employees',
                title: 'Employees',
                templateUrl: 'app/admin/views/employees.client.view.html'
            })
            .state('admin.employee', {
                url: '/employee/:employeeId',
                title: 'Employee',
                templateUrl: 'app/admin/views/employee.client.view.html'
            })
        ;
	}
]);
