'use strict';

// Admins main controller
angular.module('admin').controller('AdminController', [
    '$scope',
    '$stateParams',
    '$location',
    'Menus',
    'Authentication',
    function($scope, $stateParams, $location, Menus, Authentication) {
        // Set sidebar details
        $scope.sidebar = Menus.getMenu('admin');
        $scope.sidebarTitle = 'Admin';

        // Expose authentication service
        $scope.authentication = Authentication;
    }
]);
