'use strict';

//Shifts service used to communicate Shifts REST endpoints
angular.module('admin').factory('Shifts', [
    '$resource',
    'AppConfig',
    function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'admin/shifts/:action/:employeeId', {
            employeeId: '@_id'
        }, {
            update: {
                method: 'PUT'
            },
            timetable: {
                method: 'GET',
                isArray: true,
                params: {
                    action: 'timetable'
                }
            }
        });
    }
]);
