'use strict';

//Shifts service used to communicate Shifts REST endpoints
angular.module('admin').factory('StaticMetrics', [
    '$resource',
    'AppConfig',
    function($resource) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'admin/static-metrics/:id', {
            id: '@_id'
        }, {
            update: {
                method: 'PUT'
            }
        });
    }
]);
