'use strict';

// Conversations controller
angular.module('conversations').controller('ConversationsController', [
    '$scope',
    '$stateParams',
    '$location',
    'Authentication',
    'Conversations',
	function($scope, $stateParams, $location, Authentication, Conversations ) {
		$scope.authentication = Authentication;

		// Create new Conversation
		$scope.create = function() {
			// Create new Conversation object
			var conversation = new Conversations ({
				name: this.name
			});

			// Redirect after save
			conversation.$save(function(response) {
				$location.path('conversations/' + response._id);

				// Clear form fields
				$scope.name = '';
			}, function(errorResponse) {
				$scope.error = errorResponse.data.message;
			});
		};

		// Remove existing Conversation
		$scope.remove = function( conversation ) {
			if ( conversation ) { conversation.$remove();

				for (var i in $scope.conversations ) {
					if ($scope.conversations [i] === conversation ) {
						$scope.conversations.splice(i, 1);
					}
				}
			} else {
				$scope.conversation.$remove(function() {
					$location.path('conversations');
				});
			}
		};

		// Update existing Conversation
		$scope.update = function() {
			var conversation = $scope.conversation ;

			conversation.$update(function() {
				$location.path('conversations/' + conversation._id);
			}, function(errorResponse) {
				$scope.error = errorResponse.data.message;
			});
		};

		// Find a list of Conversations
		$scope.find = function() {
			$scope.conversations = Conversations.query();
		};

		// Find existing Conversation
		$scope.findOne = function() {
			$scope.conversation = Conversations.get({ 
				conversationId: $stateParams.conversationId
			});
		};
	}
]);