(function(){
    'use strict';

    angular.module('core').directive('linkParkopedia', linkParkopedia);

    /** @ngInject */
    function linkParkopedia() {
        return {
            restrict: 'E',
            require: 'address',
            scope: {
                address: '='
            },
            replace: true,
            template: '<a title="Parking" data-ng-href="http://en.parkopedia.nl/parking/{{address}}" target="_blank"><i class="fa fa-car"></i> Parking</a>'
        };
    }

})();


