(function(){
    'use strict';

    angular.module('core').directive('linkUberEstimate', linkUberEstimate);

    /** @ngInject */
    function linkUberEstimate() {
        return {
            restrict: 'E',
            require: 'address',
            scope: {
                address: '='
            },
            replace: true,
            template: '<a title="Uber Estimate" data-ng-href="https://uberfarefinder.com/estimate/{{address}}/{{address}}" target="_blank"><i class="fa fa-taxi"></i> Uber Estimate</a>'
        };
    }

})();


