(function(){
    'use strict';

    angular.module('core').factory('Events', Events);

    /** @ngInject **/
    function Events($rootScope, Analytics, ReceptionistsBlessings, OnlineReceptionists, TeamUpdates) {
        return {
            registerAllListeners: registerAllListeners,
            emit: emit
        };

        /////

        function registerAllListeners(){
            Analytics.registerListeners();
            ReceptionistsBlessings.registerListeners();
            OnlineReceptionists.registerListeners();
            TeamUpdates.registerListeners();
        }

        function emit(e, args){
            $rootScope.$emit(e, args);
        }

    }

})();
