'use strict';

angular.module('core').factory('gNotify', ['notify',
    function(notify) {

        function showNotify(message, classes) {
            notify({
                message: message,
                classes: classes
            });
        }
        return {
            alert: function(message) {
                showNotify(message,'alert-danger');
            },
            error: function(message) {
                showNotify(message,'alert-danger');
            },
            success: function(message) {
                showNotify(message,'alert-success');
            },
            warning: function(message) {
                showNotify(message,'alert-warning');
            },
            info: function(message) {
                showNotify(message,'alert-info');
            },
            notify: notify
        };
    }
]);
