(function(){
	'use strict';

	angular.module('core').service('PlanTypeNameHumanizer', Humanizer);

	function Humanizer(Enums){
		function humanizePlanType(val){
			var PLAN_TYPES = Enums.PLAN_TYPES;
			var result = '';
			switch(val){
				case (PLAN_TYPES.LISTING_MONTHLY):
					result = 'Per listing';
					break;
				case (PLAN_TYPES.ACCOUNT_MONTHLY):
					result = 'Monthly';
					break;
				case (PLAN_TYPES.TRANSACTION_COMMISSION):
					result = 'Per reservation';
					break;
				case (PLAN_TYPES.RESERVATION_COMMISSION):
					result = 'Per reservation';
					break;
				default:
					result = 'Per listing';
					break;
			}
			return result;
		}

		function humanizePlanName(val){
			var safeValue = val + '';
			var result = safeValue.charAt(0).toUpperCase();
			result += safeValue.substr(1, val.length).toLowerCase().replace(/_/, ' ');
			return result;
		}

		return {
			humanize: function(type, val){
				var relevantHumanizer = type === 'NAME' ? humanizePlanName : humanizePlanType;
				return relevantHumanizer(val);
			}
		};
	}
})();
