(function(){
    'use strict';

    angular.module('core').factory('Search', Search);

    /** @ngInject **/
    function Search($http, AppConfig) {
        return {
            query: query
        };

        ////////////////////

        function query(q){
            var baseApiOldUrl = AppConfig.API_OLD_URL;
            return $http.get(baseApiOldUrl + 'accounts/typeahead', {
                params: {q: q}
            });
        }

    }

})();
