'use strict';

/** todo: this is TEMP. i don't want to have a separate service for reservation requests **/

//Dashboard service used to communicate Dashboard REST endpoints
angular.module('dashboard').factory('ReservationRequests', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'reservation-requests/:reservationRequestId/:action', {
			reservationRequestId: '@_id'
		}, {
			approval: {
				method: 'PUT',
				params: {
					action: 'approval'
				}
			}
		});
	}
]);
