'use strict';

//Dashboard service used to communicate Dashboard REST endpoints
angular.module('dashboard').factory('Dashboard', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'dashboard/:dashboardId/:action', {
			dashboardId: '@_id'
		}, {
			update: {
				method: 'PUT'
			},
			report: {
				method: 'POST',
				params: {
					action: 'report'
				}
			},
			share: {
				method: 'POST',
				params: {
					action: 'share'
				}
			},
			forwardToZendesk: {
				method: 'POST',
				params: {
					action: 'zendesk'
				}
			}
		});
	}
]);
