'use strict';

(function(){
	var onboardingStatus = {
		bindings: {
			selected: '<',
			onChange: '&',
			limitRole: '@?',
		},
		templateUrl: 'app/employees/components/onboarding-status.html',
		controller: controller,
		controllerAs: 'ctrl',
	};

	/* @ngInject */
	function controller(Employees){
		var self = this;

		self.states = [
			'Revisit later',
			'Needs email onboarding',
			'Call complete',
			'Needs onboarding session',
			'Passed to support',
			'AOB complete',
			'Needs follow up',
			'Deal lost',
			'Connected Airbnb'
		].map(function(status){
			return {name: status};
		});
		self.loadResults = loadResults;
		function loadResults(){
			return self.state;
		}
	}

	angular.module('employees').component('onboardingStatus', onboardingStatus);
})();
