'use strict';

// Setting up route
angular.module('employees').config(['$stateProvider',
	function ($stateProvider) {
		// Employees state routing
		$stateProvider
            .state('login', {
				url: '/login',
				title: 'Login',
				templateUrl: 'app/employees/views/login.client.view.html'
			})
            .state('my-tickets', {
                url: '/my-tickets',
                title: 'My Tickets',
                templateUrl: 'app/employees/views/my-tickets.client.view.html'
            })
        ;
	}
]);
