'use strict';

// Employees shifts service used for communicating with the employees shifts REST endpoint
angular.module('employees').factory('EmployeesShifts', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'employees/me/shift/:action', {
        }, {
            currentShift: {
                method: 'GET',
                params: {
                    url: baseApiOldUrl + 'employees/:employeeId/shift'
                }
            },
            checkIn: {
                method: 'POST',
                params: {
                    action: 'check-in'
                }
            },
            checkOut: {
                method: 'POST',
                params: {
                    action: 'check-out'
                }
            }
		});
	}
]);
