'use strict';

//Dashboard service used to communicate ServiceTeamLogs REST endpoints
angular.module('employees').factory('ServiceTeamLogs', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'service-team-logs', {
			logId: '@_id'
		}, {
			update: {
				method: 'PUT'
			}
		});
	}
]);
