'use strict';

angular.module('hooks').filter('hookWhen', function(){
    return function(when){
        if (!when) return 'when';
        var number = parseInt(when);
        if(!number) return 'when';
        var str = Math.abs(number) + ' ' + when.replace(/[+-]?\d+\s/, '');
        if (number < 0) str += ' before';
        else str += ' after';
        return str;
    };
});
