'use strict';

//Hooks service used to communicate Hooks REST endpoints
angular.module('hooks').factory('Hooks', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'hooks/:hookId/:action', { hookId: '@_id'
		}, {
			update: {
				method: 'PUT'
			},
            updateInheritance: {
                method: 'POST',
                params: {
                    action: 'inheritance'
                }
            }
		});
	}
]);