'use strict';

//Inbox service used to communicate Inbox REST endpoints
angular.module('inbox').factory('InboxModals', ['$uibModal',
	function($uibModal) {
		return {
            openPassTicketModal: function(ticketId) {
                return $uibModal.open({
                    windowClass: 'pass-ticket-modal',
                    templateUrl: 'app/inbox/views/modals/pass-ticket-modal.client.view.html',
                    controller:  'PassTicketModalController',
                    resolve: {
                        ticketId: function() {
                            return ticketId || null;
                        }
                    }
                }).result;
            },
			openGoodTicketReviewModal: function(ticket) {
				return $uibModal.open({
					templateUrl: 'app/inbox/views/modals/qa-ticket-positive-review-modal.html',
					controller: 'ticketReviewModalController as reviewModalCtrl',
					size: 'sm',
					windowClass: 'modal-padded ticket-review-modal',
					resolve: {
						ticket: function() {
							return ticket;
						}
					}
				}).result;
			},
			openBadTicketReviewModal: function(ticket) {
				return $uibModal.open({
					templateUrl: 'app/inbox/views/modals/qa-ticket-negative-review-modal.html',
					controller: 'ticketReviewModalController as reviewModalCtrl',
					size: 'sm',
					windowClass: 'modal-padded ticket-review-modal',
					resolve: {
						ticket: function() {
							return ticket;
						}
					}
				}).result;
			},
        };
	}
]);
