'use strict';

//Inbox service used to communicate Inbox ticket REST endpoints
angular.module('inbox').factory('InboxTicket', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'inbox/ticket/:ticketId/:action', {
            'ticketId': '@_id'
        }, {
			update: {
                method: 'PUT'
            },
            done: {
                method: 'POST',
                params: {
                    action: 'done'
                }
            },
            pass: {
                method: 'POST',
                params: {
                    action: 'pass'
                }
            },
            extendAssignment: {
                method: 'POST',
                params: {
                    action: 'extend-assignment'
                }
            },
            snooze: {
                method: 'POST',
                params: {
                    action: 'snooze'
                }
            }
		});
	}
]);
