'use strict';

//Inbox service used to communicate Inbox REST endpoints
angular.module('inbox').factory('Inbox', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'inbox/:action', {}, {
			start: {
                method: 'POST',
                params: {
                    action: 'start'
                }
            },
            pause: {
                method: 'POST',
                params: {
                    action: 'pause'
                }
            }
		});
	}
]);
