'use strict';

//Setting up route
angular.module('integrations').config(['$stateProvider',
	function($stateProvider) {

		// Accounts state routing
		$stateProvider.
            state('account.integration', {
                url: '/integrations/:integrationId',
                templateUrl: 'app/integrations/views/integration.client.view.html',
                controller: 'IntegrationController'
            })
        ;
	}
]);
