'use strict';

angular.module('integrations').controller('RentalsUnitedIntegrationController', [
    '$scope',
    'Integrations',
    'notify',
    '$uibModal',
    function($scope, Integrations, notify, $uibModal) {

        var self = this;

        self.importListings = function(){
            $uibModal.open({
                templateUrl: 'app/integrations/views/create-rentals-united2.modal.client.view.html',
                controller: 'CreateNewRentalsUnited2Controller as ru',
                resolve: {
                    integration: function() {
                        return $scope.integration;
                    },
                    accountId: function(){
                        return $scope.integration.accountId;
                    }
                }
            });
        };

    }
]);
