'use strict';

//Internal notes service used to communicate Internal notes REST endpoints
angular.module('internal-notes').factory('InternalNotes', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'internal-notes/:reservationId', { reservationId: '@reservationId'
		}, {
			update: {
				method: 'PUT'
			}
		});
	}
]);
