'use strict';

// invoice items modals service
angular.module('invoice-items').factory('InvoiceItemsModalService', ['$uibModal',
    function($uibModal) {

        return {
            /**
             * modal to create an invoice item for a reservation
             * @param accountId {String}
             * @param [reservationId] {String}
             * @returns {promise}
             */
            openCreateInvoiceItemModal: function(accountId, reservationId) {
                return $uibModal.open({
                    templateUrl: 'app/invoice-items/views/create-invoice-item-modal.client.view.html',
                    controller: 'CreateInvoiceItemModalController',
                    resolve: {
                        formData: function() {
                            return {
                                accountId: accountId,
                                reservationId: reservationId
                            };
                        }
                    }
                }).result;
            }
        };
    }
]);
