'use strict';

//Invoices service used to communicate Invoices REST endpoints
angular.module('invoices').factory('Invoices', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

		return $resource(baseApiOldUrl + 'invoices/:invoiceId/:action', { invoiceId: '@_id'
		}, {
			refund: {
                params: {
                    action: 'refund'
                },
				method: 'POST'
			}
		});
	}
]);
