'use strict';

// listings preferences controller
angular.module('listings').controller('ListingReceptionistsItttModalController', [
	'$scope',
    'ittt',
    '$uibModalInstance',
	function($scope, ittt, $uibModalInstance) {

        var self = this;

        self.ifThis = ittt.ifThis;
        self.thenThat = ittt.thenThat;

        self.delete = function(){
            $uibModalInstance.close({
                action: 'delete'
            });
        };

        self.save = function(){
            $uibModalInstance.close({
                action: 'update',
                ifThis: self.ifThis,
                thenThat: self.thenThat
            });
        };

	}
]);
