'use strict';

//Listing service used to communicate Listing REST endpoints
angular.module('listings').factory('ListingCalendar', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;

		return $resource(baseApi2Url + '/listings/:listingId/calendar', { listingId: '@listingId' }, {
            retrieve: {
                method: 'GET',
                isArray: true
            },
            retrieveMulti: {
                url: baseApi2Url + '/listings/calendars',
                method: 'GET',
                isArray: true
            },
			update: {
                url: baseApi2Url + 'listings/calendars',
                method: 'PUT'
            }
		});
	}
]);
