'use strict';

//Setting up route
angular.module('notifications').config(['$stateProvider',
	function($stateProvider) {
		// Notifications state routing
		$stateProvider.
            state('notifications', {
                abstract: true,
                url: '/notifications',
                template: '<ui-view/>'
            }).
            state('notifications.view', {
                url: '/:notificationId',
                title: 'Notification view',
                templateUrl: 'app/notifications/views/view-notification.client.view.html'
            })
        ;
	}
]);
