'use strict';

//Reservations service used to communicate Reservations REST endpoints
angular.module('post-bookings').factory('PostBookingTickets', [
    '$resource',
    'AppConfig',
    function ($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;
        
        return $resource(baseApiOldUrl + 'post-bookings/:ticketId/:action', { ticketId: '@_id'
		}, {
			save: {
				method: 'PUT'
			}
		});
    }
]);
