(function(){
    'use strict';

    angular.module('receptionists').factory('ReceptionistsBlessings', ReceptionistsBlessings);

    /** @ngInject **/
    function ReceptionistsBlessings(SweetAlert, AppConfig, $rootScope) {

        return {
            registerListeners: registerListeners
        };

        function registerListeners(){
            $rootScope.$on('employee.checkedOut', onCheckedOut);
        }

        function onCheckedOut(){
            var blessings = AppConfig.receptionists.checkOutBlessings;
            var index = _.random(blessings.length-1);
            SweetAlert.swal(blessings[index], null, 'success');
        }

    }

})();
