'use strict';

angular.module('research').factory('ResearchModalService', [ '$uibModal',
	function($uibModal) {
		// Research modal service logic

		// Public API
		return {
			open: function(metadata ,query) {
				return $uibModal.open({
					templateUrl: 'app/research/views/research-modal.client.view.html',
					controller: 'ResearchModalController',
					size: 'lg',
					windowClass: 'research-modal modal-padded',
					resolve: {
						metadata: function () {
							return metadata;
						},
						query: function () {
							return query || null;
						}
					}
				}).result;
			}
		};
	}
]);
