'use strict';

//Setting up route
angular.module('reservations').config(['$stateProvider',
    function ($stateProvider) {
        var searchParams = ['filter', 'checkIn', 'checkOut', 'canceledAt', 'confirmedAt', 'guestId', 'account', 'accountId', 'listingId'];

        // Reservations state routing
        $stateProvider.
            state('reservations', {
                abstract: true,
                url: '/reservations',
                templateUrl: 'app/reservations/views/reservations.client.view.html'
            })
            .state('pending-requests', {
                url: '/pending',
                title: 'Pending Requests',
                templateUrl: 'app/reservations/views/pending-requests.client.view.html'
            })
            .state('reservations.search', {
                url: '/search?' + searchParams.join('&'),
                title: 'Search Reservations',
                templateUrl: 'app/reservations/views/reservations-search.client.view.html'
            })
            .state('reservations.reservation', {
                url: '/:reservationId',
                title: 'Reservation',
                templateUrl: 'app/reservations/views/reservation/reservation.client.view.html'
            });
    }
]);
