'use strict';

// Reservations controller
angular.module('reservations').controller('PostBookingBoxController', [
    '$scope',
    function($scope) {

        $scope.getColor = function(status, extra){
            if (!$scope.reservation.postBooking) return 'btn-default';
            if (!extra) extra = '';
            return extra + $scope.reservation.postBooking['color' + status] || 'btn-default';
        };

        $scope.updateAllColors = function(color){
            $scope.reservation.postBooking = $scope.reservation.postBooking || {};
            $scope.reservation.postBooking.colorHost = color;
            $scope.reservation.postBooking.colorGuest = color;
            $scope.reservation.postBooking.colorCleaning = color;
            $scope.updateReservation(null, 'postBooking');
        };

        $scope.updateColor = function(status, color) {
            $scope.reservation.postBooking = $scope.reservation.postBooking || {};
            $scope.reservation.postBooking['color' + status] = color;
            $scope.updateReservation(null, 'postBooking');
        };
    }
]);
