'use strict';

//Reservations Cleaning Appointments service used to communicate Reservations REST endpoints
angular.module('reservations').factory('ReservationsCleaningAppointments', [
    '$resource',
    'AppConfig',
    function ($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'reservations/:reservationId/cleaning-appointments/:appointmentId',
            {
                appointmentId: '@_id',
                reservationId: '@reservationId'
            }, {
                update: {
                    method: 'PUT'
                }
            });
    }
]);
