'use strict';

//Reservations hooks service used to communicate Reservations hooks REST endpoints
angular.module('reservations').factory('ReservationsHooks', [
    '$resource',
    'AppConfig',
    function ($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;

        return $resource(baseApiOldUrl + 'reservations/:reservationId/hooks/:reservationHookId',
            {
                reservationHookId: '@_id',
                reservationId: '@reservationId'
            },
            {
                update: {
                    method: 'PUT'
                }
            });
    }
]);
