'use strict';

//Reservations Reviews service used to communicate Reservations REST endpoints
angular.module('reservations').factory('ReservationsReviews', [
    '$resource',
    'AppConfig',
	function($resource, AppConfig) {

        var baseApi2Url = AppConfig.API_URL;
        var baseApiOldUrl = AppConfig.API_OLD_URL;
        
		return $resource(baseApiOldUrl + 'reservations/:reservationId/review', {
			reservationId: '@reservationId'
		}, {
            update: {
                method: 'PUT'
            }
		});
	}
]);
